import httpx
import os
from youtubesearchpython.core.constants import userAgent

class RequestCore:
    def __init__(self):
        self.url = None
        self.data = None
        self.timeout = 2
        self.proxy = {}

        # قراءة إعدادات البروكسي من المتغيرات البيئية
        http_proxy = os.environ.get("HTTP_PROXY")
        if http_proxy:
            self.proxy["http"] = http_proxy
        https_proxy = os.environ.get("HTTPS_PROXY")
        if https_proxy:
            self.proxy["https"] = https_proxy

    def syncPostRequest(self) -> httpx.Response:
        """إرسال طلب POST متزامن"""
        return httpx.post(
            self.url,
            headers={"User-Agent": userAgent},
            json=self.data,
            timeout=self.timeout
        )

    async def asyncPostRequest(self) -> httpx.Response:
        """إرسال طلب POST غير متزامن"""
        async with httpx.AsyncClient() as client:
            r = await client.post(
                self.url,
                headers={"User-Agent": userAgent},
                json=self.data,
                timeout=self.timeout
            )
            return r

    def syncGetRequest(self) -> httpx.Response:
        """إرسال طلب GET متزامن"""
        return httpx.get(
            self.url,
            headers={"User-Agent": userAgent},
            timeout=self.timeout
        )

    async def asyncGetRequest(self) -> httpx.Response:
        """إرسال طلب GET غير متزامن"""
        async with httpx.AsyncClient() as client:
            r = await client.get(
                self.url,
                headers={"User-Agent": userAgent},
                timeout=self.timeout
            )
            return rr
